import { Button } from "@/components/ui/button";
import { Checkbox } from "@/components/ui/checkbox";
import { Label } from "@/components/ui/label";
import { MockCartItem } from "@/lib/mockApi";
import { Minus, Plus, Trash2, Package } from "lucide-react";
import { useState, useEffect } from "react";

interface CartItemProps {
  item: MockCartItem;
  onQuantityChange: (id: number, quantity: number) => void;
  onRemove: (id: number) => void;
  onUpdateSubstitution: (id: number, allowSubstitutions: boolean) => void;
}

export const CartItem = ({ item, onQuantityChange, onRemove, onUpdateSubstitution }: CartItemProps) => {
  const [allowSubstitutions, setAllowSubstitutions] = useState<boolean>(item.allow_substitutions ?? true);
  const [substitutionRate, setSubstitutionRate] = useState<number | null>(null);

  // Fetch substitution rate for this product
  useEffect(() => {
    if (item.product_id) {
      const fetchSubstitutionRate = async () => {
        try {
          const response = await fetch(`/api/product/${item.product_id}/substitution-rate`);
          if (response.ok) {
            const data = await response.json();
            setSubstitutionRate(data.substitutionRate);
            // Set default based on substitution rate if not already set
            if (item.allow_substitutions === undefined) {
              const defaultValue = data.substitutionRate > 0.08;
              setAllowSubstitutions(defaultValue);
              onUpdateSubstitution(item.id, defaultValue);
            }
          }
        } catch (error) {
          console.error('Failed to fetch substitution rate:', error);
          setSubstitutionRate(0.05); // Default rate - low so substitutions are not enabled by default
        }
      };

      fetchSubstitutionRate();
    }
  }, [item.product_id, item.allow_substitutions, item.id, onUpdateSubstitution]);

  const handleSubstitutionChange = (checked: boolean) => {
    setAllowSubstitutions(checked);
    onUpdateSubstitution(item.id, checked);
  };

  return (
    <div className="p-4 border border-gray-200 rounded-lg hover:border-gray-300 transition-colors">
      <div className="flex items-center justify-between mb-3">
        <div className="flex-1">
          <h3 className="font-medium text-gray-900">{item.product_name}</h3>
          <p className="text-xs text-gray-500 mb-1">{item.brand}</p>
          <p className="text-sm text-gray-600">${item.price_per_unit.toFixed(2)} each</p>
        </div>
        <div className="flex items-center space-x-3">
          <div className="flex items-center border border-gray-300 rounded-lg">
            <Button
              variant="ghost"
              size="sm"
              className="p-2 hover:bg-gray-100 transition-colors"
              onClick={() => onQuantityChange(item.id, item.quantity - 1)}
            >
              <Minus className="w-3 h-3 text-gray-600" />
            </Button>
            <span className="px-3 py-2 text-sm font-medium">{item.quantity}</span>
            <Button
              variant="ghost"
              size="sm"
              className="p-2 hover:bg-gray-100 transition-colors"
              onClick={() => onQuantityChange(item.id, item.quantity + 1)}
            >
              <Plus className="w-3 h-3 text-gray-600" />
            </Button>
          </div>
          <Button
            variant="ghost"
            size="sm"
            className="text-red-500 hover:text-red-700 p-2"
            onClick={() => onRemove(item.id)}
          >
            <Trash2 className="w-4 h-4" />
          </Button>
        </div>
      </div>

      {/* Substitution preference - always show, default based on rates */}
      {substitutionRate !== null && (
        <div className="flex items-center text-xs text-gray-600">
          <Checkbox
            id={`substitution-${item.id}`}
            checked={allowSubstitutions}
            onCheckedChange={handleSubstitutionChange}
            className="mr-2"
          />
          <Label htmlFor={`substitution-${item.id}`} className="flex items-center cursor-pointer">
            <Package className="w-3 h-3 mr-1" />
            {allowSubstitutions ? "Allow substitutions" : "Do not substitute"}
          </Label>
        </div>
      )}
    </div>
  );
};

client/src/components/CheckoutView.tsx.content:
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Label } from "@/components/ui/label";

import { useAppContext } from "@/contexts/AppContext";
import { CreditCard, Clock, Calendar, Package } from "lucide-react";
import { useState, useEffect } from "react";

interface CheckoutViewProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

export const CheckoutView = ({ open, onOpenChange }: CheckoutViewProps) => {
  const { cartItems, cartTotal, deliveryPredictions, activeUserID } = useAppContext();
  const [deliveryMethod, setDeliveryMethod] = useState(deliveryPredictions?.predicted_method || "delivery");
  const [isLoadingPreference, setIsLoadingPreference] = useState(false);
  const [deliveryTimes, setDeliveryTimes] = useState<any[]>([]);
  const [selectedTimeSlot, setSelectedTimeSlot] = useState<string>("");
  const [isLoadingTimes, setIsLoadingTimes] = useState(false);


  const deliveryFee = deliveryMethod === "delivery" ? 3.99 : 0;
  const finalTotal = cartTotal + deliveryFee;

  // Fetch user's last delivery preference and time slots when modal opens
  useEffect(() => {
    if (open && activeUserID) {
      fetchUserDeliveryPreference();
      fetchDeliveryTimes();
    }
  }, [open, activeUserID]);

  const fetchUserDeliveryPreference = async () => {
    try {
      setIsLoadingPreference(true);
      const response = await fetch(`/api/user/${activeUserID}/delivery-preference`);
      if (response.ok) {
        const data = await response.json();
        setDeliveryMethod(data.deliveryMethod);
      }
    } catch (error) {
      console.error('Failed to fetch delivery preference:', error);
    } finally {
      setIsLoadingPreference(false);
    }
  };

  const updateUserDeliveryPreference = async (method: string) => {
    try {
      await fetch(`/api/user/${activeUserID}/delivery-preference`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ deliveryMethod: method }),
      });
    } catch (error) {
      console.error('Failed to update delivery preference:', error);
    }
  };

  const fetchDeliveryTimes = async () => {
    try {
      setIsLoadingTimes(true);
      console.log('Fetching delivery times for user:', activeUserID);

      // Get user's timezone
      const userTimezone = Intl.DateTimeFormat().resolvedOptions().timeZone;

      const response = await fetch(`/api/predict/delivery-times/${activeUserID}?timezone=${encodeURIComponent(userTimezone)}`);
      if (response.ok) {
        const times = await response.json();
        console.log('Received delivery times:', times);
        setDeliveryTimes(times);
        // Auto-select the first (highest-scored) time slot
        if (times.length > 0) {
          setSelectedTimeSlot(`${times[0].date_label}-${times[0].time_window}`);
        }
      } else {
        console.error('Failed to fetch delivery times:', response.status);
      }
    } catch (error) {
      console.error('Failed to fetch delivery times:', error);
    } finally {
      console.log('Setting loading to false');
      setIsLoadingTimes(false);
    }
  };

  const handleDeliveryMethodChange = (method: string) => {
    setDeliveryMethod(method);
    updateUserDeliveryPreference(method); // Save preference immediately
  };

  const handleCompleteOrder = () => {
    // Handle order completion
    alert("Order completed successfully!");
    onOpenChange(false);
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-4xl">
        <DialogHeader>
          <DialogTitle className="flex items-center">
            <CreditCard className="w-5 h-5 text-primary mr-2" />
            Checkout
          </DialogTitle>
        </DialogHeader>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Order Summary */}
          <div>
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Order Summary</h3>
            <div className="space-y-3 mb-6">
              {cartItems.map(item => (
                <div key={item.id} className="border-b border-gray-100 pb-3 mb-3">
                  <div className="flex justify-between items-center">
                    <span className="text-gray-700">{item.product_name} ({item.quantity}x)</span>
                    <span className="font-medium">${(item.price_per_unit * item.quantity).toFixed(2)}</span>
                  </div>
                  {item.allow_substitutions !== undefined && (
                    <div className="flex items-center mt-1 text-xs text-gray-500">
                      <Package className="w-3 h-3 mr-1" />
                      {item.allow_substitutions ? "Substitutions allowed" : "No substitutions"}
                    </div>
                  )}
                </div>
              ))}
            </div>

            <div className="border-t border-gray-200 pt-4 space-y-2">
              <div className="flex justify-between items-center">
                <span>Subtotal:</span>
                <span className="font-medium">${cartTotal.toFixed(2)}</span>
              </div>
              <div className="flex justify-between items-center">
                <span>Delivery Fee:</span>
                <span className="font-medium">${deliveryFee.toFixed(2)}</span>
              </div>
              <div className="flex justify-between items-center text-lg font-semibold">
                <span>Total:</span>
                <span className="text-primary">${finalTotal.toFixed(2)}</span>
              </div>
            </div>
          </div>

          {/* Delivery Options */}
          <div>
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Delivery Options</h3>
            <RadioGroup value={deliveryMethod} onValueChange={handleDeliveryMethodChange} className="space-y-3 mb-6">
              <div className={`border rounded-lg p-4 ${deliveryMethod === "delivery" ? "border-primary bg-green-50" : "border-gray-300"}`}>
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    <RadioGroupItem value="delivery" id="delivery" />
                    <div>
                      <Label htmlFor="delivery" className="font-medium text-gray-900">
                        Delivery
                        {deliveryPredictions?.predicted_method === "delivery" && (
                          <span className="ml-2 bg-primary text-white text-xs px-2 py-1 rounded-full">
                            Recommended
                          </span>
                        )}
                      </Label>
                      <p className="text-sm text-gray-600">
                        {deliveryPredictions?.predicted_time_window || "Next available slot"}
                      </p>
                    </div>
                  </div>
                  <span className="font-medium text-gray-900">$3.99</span>
                </div>
              </div>

              <div className={`border rounded-lg p-4 ${deliveryMethod === "pickup" ? "border-primary bg-green-50" : "border-gray-300"}`}>
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    <RadioGroupItem value="pickup" id="pickup" />
                    <div>
                      <Label htmlFor="pickup" className="font-medium text-gray-900">
                        Pickup
                        {deliveryPredictions?.predicted_method === "pickup" && (
                          <span className="ml-2 bg-primary text-white text-xs px-2 py-1 rounded-full">
                            Recommended
                          </span>
                        )}
                      </Label>
                      <p className="text-sm text-gray-600">Store pickup available</p>
                    </div>
                  </div>
                  <span className="font-medium text-gray-900">Free</span>
                </div>
              </div>
            </RadioGroup>

            {/* Delivery Time Selection */}
            {deliveryMethod === "delivery" && (
              <div className="mt-6">
                <h4 className="text-md font-medium text-gray-900 mb-3 flex items-center">
                  <Clock className="w-4 h-4 mr-2" />
                  Select Delivery Time
                </h4>

                {isLoadingTimes ? (
                  <div className="text-sm text-gray-600">Loading optimal time slots...</div>
                ) : (
                  <div className="space-y-2">
                    <RadioGroup value={selectedTimeSlot} onValueChange={setSelectedTimeSlot} className="space-y-2">
                      {deliveryTimes.map((slot, index) => (
                        <div key={`${slot.date_label}-${slot.time_window}`} 
                             className={`border rounded-lg p-3 ${selectedTimeSlot === `${slot.date_label}-${slot.time_window}` ? "border-primary bg-green-50" : "border-gray-300"}`}>
                          <div className="flex items-center justify-between">
                            <div className="flex items-center space-x-3">
                              <RadioGroupItem value={`${slot.date_label}-${slot.time_window}`} id={`slot-${index}`} />
                              <div>
                                <Label htmlFor={`slot-${index}`} className="font-medium text-gray-900 flex items-center">
                                  <Calendar className="w-3 h-3 mr-1" />
                                  {slot.date_label} {slot.time_window}
                                  {index === 0 && (
                                    <span className="ml-2 bg-primary text-white text-xs px-2 py-1 rounded-full">
                                      AI Recommended
                                    </span>
                                  )}
                                </Label>
                                <p className="text-xs text-gray-600">
                                  Personalized prediction based on your history
                                </p>
                              </div>
                            </div>
                          </div>
                        </div>
                      ))}
                    </RadioGroup>

                    <Button variant="outline" className="w-full mt-3" disabled>
                      Choose another delivery day/time (Coming Soon)
                    </Button>
                  </div>
                )}
              </div>
            )}

            <Button 
              onClick={handleCompleteOrder}
              className="w-full bg-primary text-white py-3 px-6 font-semibold hover:bg-primary-dark transition-colors mt-6"
            >
              Complete Order - ${finalTotal.toFixed(2)}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
};

client/src/components/InspireMeModal.tsx.content:
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger, DialogDescription } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { useAppContext } from "@/contexts/AppContext";
import { useToast } from "@/hooks/use-toast";
import { Wand2, Loader2, CheckCircle, Heart, BookOpen } from "lucide-react";
import { useState, useEffect } from "react";

export const InspireMeModal = () => {
  const { generateRecipes, addToCart, aiGeneratedRecipes, isGeneratingRecipes, cartItems } = useAppContext();
  const { toast } = useToast();
  const [open, setOpen] = useState(false);
  const [addedItems, setAddedItems] = useState<Set<string>>(new Set());
  const [savedRecipes, setSavedRecipes] = useState<Set<string>>(new Set());

  // Generate recipes only when modal first opens and clear added items
  useEffect(() => {
    if (open && cartItems.length > 0 && aiGeneratedRecipes.length === 0) {
      generateRecipes();
    }
    if (open) {
      setAddedItems(new Set()); // Clear added items when modal opens
    }
  }, [open]);

  const handleAddItem = (itemName: string, price: number) => {
    addToCart({ 
      product_id: Date.now(), 
      product_name: itemName, 
      price_per_unit: price,
      category: "Grocery",
      brand: "Fresh Market",
      size: "1 unit",
      unit: "unit"
    });

    // Add visual feedback
    setAddedItems(prev => new Set([...Array.from(prev), itemName]));
    toast({
      title: "Added to cart!",
      description: `${itemName} - $${price.toFixed(2)}`,
      duration: 2000,
    });

    // Remove the added state after 3 seconds
    setTimeout(() => {
      setAddedItems(prev => {
        const newSet = new Set(prev);
        newSet.delete(itemName);
        return newSet;
      });
    }, 3000);
  };

  const handleAddAllItems = (items: Array<any>) => {
    items.forEach(item => {
      addToCart({ 
        product_id: item.product_id || Date.now(), 
        product_name: item.product_name, 
        price_per_unit: item.price_per_unit,
        category: item.category || "Grocery",
        brand: item.brand || "Fresh Market",
        size: item.size || "1 unit",
        unit: item.unit || "unit"
      });

      // Add visual feedback for each item
      setAddedItems(prev => new Set([...Array.from(prev), item.product_name]));
    });

    const totalPrice = items.reduce((sum, item) => sum + item.price_per_unit, 0);
    toast({
      title: `Added ${items.length} items to cart!`,
      description: `Total: $${totalPrice.toFixed(2)}`,
      duration: 3000,
    });

    // Remove added states after 3 seconds
    setTimeout(() => {
      setAddedItems(new Set());
    }, 3000);
  };

  const handleSaveRecipe = (recipeTitle: string) => {
    setSavedRecipes(prev => new Set([...Array.from(prev), recipeTitle]));
    toast({
      title: "Recipe saved!",
      description: `"${recipeTitle}" has been saved to your recipe collection`,
      duration: 3000,
    });

    // In a real app, this would save to a database or local storage
    console.log('Saving recipe:', recipeTitle);
  };

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogTrigger asChild>
        <Button className="bg-accent text-white px-4 py-2 text-sm font-medium hover:bg-yellow-600 transition-colors">
          âœ¨ Inspire Me
        </Button>
      </DialogTrigger>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-hidden flex flex-col">
        <DialogHeader>
          <DialogTitle className="flex items-center">
            <Wand2 className="w-5 h-5 text-accent mr-2" />
            Recipe Inspiration
          </DialogTitle>
          <DialogDescription>
            AI-generated recipes based on your cart items. Add ingredients directly to your cart!
          </DialogDescription>
        </DialogHeader>

        <div className="overflow-y-auto flex-1 p-1">
